CREATE TABLE "franchises" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "franchise_id" varchar UNIQUE,
  "poc_name" varchar,
  "primary_number" varchar,
  "email" varchar,
  "address" text,
  "latitude" decimal(10,6),
  "longitude" decimal(10,6),
  "city" varchar,
  "state" varchar,
  "pincode" varchar,
  "porter_hub_name" varchar,
  "franchise_gst" varchar,
  "franchise_pan" varchar,
  "franchise_canceled_cheque" varchar,
  "status" varchar CHECK ("status" IN ('Active', 'Inactive', 'Suspended')),
  "team_id" int,
  "days_of_operation" varchar,
  "start_time" time,
  "end_time" time,
  "cut_off_time" time,
  "kam_user" varchar,
  "hlp_enabled" boolean,
  "radius_coverage" decimal(10,2),
  "show_cr_number" boolean,
  "created_at" timestamp DEFAULT now(),
  "updated_at" timestamp DEFAULT now()
);